--Loops the script.
---@param milliseconds? integer The number of milliseconds between loops. Default is 16 (once every frame).
function loop(milliseconds) end

--Halts the script for the specified amount of time.
---@param milliseconds integer The number of milliseconds to wait
function wait(milliseconds) end

--Executes the specified script.
---@param script string The script to execute.
function script(script) end

--Displays a messagebox with four options. Pass an empty string to any option to remove it.
---@param heading string The title for this messagebox.
---@param body string The body test for this messagebox.
---@param a_text string The text for the A button.
---@param x_text string The text for the X button.
---@param y_text string The text for the Y button.
---@param b_text string The text for the B button.
---@param vertical boolean If true, the button on the message box will be listed vertically.
---@param no_cancel boolean If true, the messagebox cannot be closed with B/Escape.
---@return integer integer The button selected.
function msgbox(heading, body, a_text, x_text, y_text, b_text, vertical, no_cancel) end

--Displays a menu and waits for user input.
---@param heading string The title for this menu.
---@param ... any The options to display. Multiple arguments can be passed, including numbers (which are converted to text.).
---@return integer integer The option selected.
function menu(heading, ...) end

--Add a falling block at the map point
---@param x integer The x component of the map point
---@param y integer The y component of the map point
---@param z integer The z component of the map point
---@param block_id integer The block id
---@param vel_x number The x velocity of the block
---@param vel_y number The y velocity of the block
---@param vel_z number The z velocity of the block
---@param gravity number A gravity multiplier
---@param break_chance number The chance the block will break on impact
function add_flying_block(x, y, z, block_id, vel_x, vel_y, vel_z, gravity, break_chance) end

--Copy block data (block id, aux, light) from one map point to another
---@param x1 integer The x component of the source map point
---@param y1 integer The y component of the source map point
---@param z1 integer The z component of the source map point
---@param x2 integer The x component of the destination map point
---@param y2 integer The y component of the destination map point
---@param z2 integer The z component of the destination map point
function copy_block(x1, y1, z1, x2, y2, z2) end

--Copy block data (block id, aux, light) from one cubic region to another
---@param x1 integer The x component of the source regions min map point
---@param y1 integer The y component of the source regions min map point
---@param z1 integer The z component of the source regions min map point
---@param x2 integer The x component of the source regions max map point
---@param y2 integer The y component of the source regions max map point
---@param z2 integer The z component of the source regions max map point
---@param x3 integer The x component of the destination regions min map point
---@param y3 integer The y component of the destination regions min map point
---@param z3 integer The z component of the destination regions min map point
function copy_region(x1, y1, z1, x2, y2, z2, x3, y3, z3) end

--Get the aux data at a map point
---@param x integer The x component of the map point
---@param y integer The y component of the map point
---@param z integer The z component of the map point
